div.script:
function show_info(evt)
 {
  var d1 = document.getElementById("info1");
  if (!d1) return;
  var ns1= "http://www.ga.gov.au";
  var elem = evt.target;
  var name = elem.getAttributeNS(ns1,"name");
  var ufi = elem.getAttributeNS(ns1,"ufi");
  var area = elem.getAttributeNS(ns1,"area");
  var blurb = '<table><tr><td>Name:<td><td>'+name+'<td><tr><tr><td>Area:<td><td>'+area+'<td><tr><tr><td>UFI:<td><td>'+ufi+'<td><tr><table>';
  d1.innerHTML = blurb;
 };
